import React from 'react';

/**
 * Card component which represents the private Poker game cards 
 * dealt to the User and the Community cards dealt on the table.
 */
const Card = (props) => {
  const {
    cardData: {
      suit,
      cardFace,
      animationDelay
    },
    setFoldedClassName
  } = props;

  // get suit of card
  var suitOfCard = suit.substring(0, 1).toUpperCase();

  var face
  switch(cardFace){
    case 'ace':
      if (cardFace == "A") {
        face = "Ace"
      }
    case 'two':
      if (cardFace == 2) {
        face = "Two"
      }
    case 'three':
      if (cardFace == "3") {
        face = "Three"
      }
    case 'three':
      if (cardFace == 4) {
        face = "Four"
      }
    case 'three':
      if (cardFace == 5) {
        face = "Five"
      }
    case 'three':
      if (cardFace == 6) {
        face = "Six"
      }
    case 'three':
      if (cardFace == 7) {
        face = "Seven"
      }
    case 'three':
      if (cardFace == 8) {
        face = "Eight"
      }
    case 'three':
      if (cardFace == 9) {
        face = "Nine"
      }
    case 'three':
      if (cardFace == 10) {
        face = "Ten"
      }
    case 'three':
      if (cardFace == "J") {
        face = "Jack"
      }
    case 'three':
      if (cardFace == "Q") {
        face = "Queen"
      }
    case 'three':
      if (cardFace == "K") {
        face = "King"
      }
  }

  // display card according to value and suit given
  return (
    <div 
      className={`poker-card cardIn ${determineName(setFoldedClassName)}`}
      style={
        {animationDelay: `${determineStall(setFoldedClassName, animationDelay)}ms` }
        }>
      <img src={`${process.env.PUBLIC_URL}/assets/cardFaces/${cardFace}${suitOfCard}.svg`} alt={`${face} of ${suit}s`}></img>
    </div>
  )
}

/* 
Selects the class name depending on 
the state of the players cards.
*/
function determineName(className){
  if (className) {
    return ' folded'
  }
  else {
    return ''
  }
}

/* 
Determines how long the stall animations 
takes to complete.
*/
function determineStall(name, time) {
  if(name) {
    return 0
  }
  else{
    return time
  }
}

export default Card;
